jQuery.fn.replaceTags=function(taglist){var tags=[];for(var tag in taglist){tags.push(tag)}for(var i in tags){this.find(tags[i].toString()).each(function(){var el=jQuery(this);var newel=jQuery("<"+taglist[tags[i]]+">");newel.attr(jQuery.getAttributes(el,true));newel.html(jQuery(el).html());jQuery(el).replaceWith(newel)})}}