function replaceTags(taglist){var tags=keys(taglist);for(var i in tags){$("body").find(tags[i].toString()).each(function(){var el=$(this);var newel=$("<"+taglist[tags[i]]+">");newel.attr($.getAttributes(el,true));newel.html($(el).html());$(el).replaceWith(newel)})}}function keys(obj){var keys=[];for(var key in obj){keys.push(key)}return keys}